mapper.select:
select t.*, found_rows() as rowsTotal from ( select a.id, a.from_uid as fromUid, a.topic_id as topicId, d.nickname, d.head_portrait as headPortrait, a.content, case a.topic_id when 2 then true else false end as errands, a.province, a.city, a.county, a.thumbs_up_num as thumbsUpNum, a.comment_num as commentNum, a.status, a.gmt_create as gmtCreate, b.topic_name as topicName, b.type, group_concat(c.image) as images from campus_information a left join campus_information_topic b on a.topic_id = b.id and b.status >= 0 left join campus_information_images c on a.id = c.information_id left join campus_user d on a.from_uid = d.id
and a.topic_id = #{topicId, jdbcType=INTEGER}
group by a.id order by a.gmt_create desc ) t
limit #{pageIndex, jdbcType=INTEGER}, #{pageCount, jdbcType=INTEGER}

mapper.update:
update campus_information
set thumbs_up_num = thumbs_up_num + 1
where id = #{id, jdbcType=INTEGER}

update campus_information
set thumbs_up_num = (case when thumbs_up_num = 0 then 0 else thumbs_up_num - 1 end)
where id = #{id, jdbcType=INTEGER}

update campus_information
set comment_num = comment_num + 1
where id = #{id, jdbcType=INTEGER}
