mapper.select:
select

from campus_user
where telphone = #{telphone} and password = #{password}

mapper.update:
update campus_user
set follows = follows + 1
where id = #{id, jdbcType=INTEGER}

update campus_user
set follows = (case when follows = 0 then 0 else follows - 1 end)
where id = #{id, jdbcType=INTEGER}

update campus_user
set concerns = concerns + 1
where id = #{id, jdbcType=INTEGER}

update campus_user
set concerns = (case when concerns = 0 then 0 else concerns - 1 end)
where id = #{id, jdbcType=INTEGER}
